import { useEffect, useState } from 'react';
import { getData, setData } from '../../utils/localStorage';
import { sampleBlogPosts, sampleBlogCategories } from '../../utils/sampleData';
import { Button } from '../ui/button';
import { toast } from 'sonner@2.0.3';
import { Download } from 'lucide-react';

export function InitializeSampleData() {
  const [hasData, setHasData] = useState(false);

  useEffect(() => {
    const blogPosts = getData('blogPosts') || [];
    setHasData(blogPosts.length > 0);
  }, []);

  const loadSampleData = () => {
    try {
      // Load sample categories if none exist
      const existingCategories = getData('blogCategories') || [];
      if (existingCategories.length === 0) {
        setData('blogCategories', sampleBlogCategories);
      }

      // Load sample blog posts
      const existingPosts = getData('blogPosts') || [];
      const newPosts = [...existingPosts, ...sampleBlogPosts];
      setData('blogPosts', newPosts);

      toast.success('Sample blog posts loaded successfully!');
      setHasData(true);

      // Refresh the page to show new data
      setTimeout(() => {
        window.location.reload();
      }, 1000);
    } catch (error) {
      toast.error('Failed to load sample data');
    }
  };

  if (hasData) return null;

  return (
    <div className="bg-blue-50 border border-blue-200 rounded-lg p-4 mb-6">
      <div className="flex items-start gap-3">
        <Download className="h-5 w-5 text-blue-600 mt-0.5" />
        <div className="flex-1">
          <h3 className="font-medium text-blue-900 mb-1">No blog posts yet</h3>
          <p className="text-sm text-blue-700 mb-3">
            Would you like to load sample blog posts with images to see how it looks?
          </p>
          <Button onClick={loadSampleData} size="sm" variant="outline">
            Load Sample Blog Posts
          </Button>
        </div>
      </div>
    </div>
  );
}

import { useState, useEffect } from 'react';
import { X, Download, Phone, Mail } from 'lucide-react';
import { Button } from '../ui/button';
import { Input } from '../ui/input';
import { Label } from '../ui/label';
import { getData, addItem } from '../../utils/localStorage';
import { toast } from 'sonner@2.0.3';
import { useTheme } from '../../contexts/ThemeContext';

export function ExitIntentPopup() {
  const [isVisible, setIsVisible] = useState(false);
  const [hasShown, setHasShown] = useState(false);
  const [settings, setSettings] = useState<any>(null);
  const [formData, setFormData] = useState({ name: '', phone: '', email: '' });
  const { colors } = useTheme();

  useEffect(() => {
    const loadSettings = async () => {
      const popupSettings = await getData('exitIntentSettings');
      if (popupSettings && !Array.isArray(popupSettings) && popupSettings.enabled) {
        setSettings(popupSettings);

        // Check if already shown in this session
        const shownInSession = sessionStorage.getItem('exitIntentShown');
        if (shownInSession) {
          setHasShown(true);
          return;
        }

        // Track mouse movement for exit intent
        let exitIntentTriggered = false;
        const handleMouseLeave = (e: MouseEvent) => {
          if (e.clientY <= 0 && !exitIntentTriggered && !hasShown) {
            exitIntentTriggered = true;
            setTimeout(() => {
              setIsVisible(true);
              setHasShown(true);
              sessionStorage.setItem('exitIntentShown', 'true');
            }, 200);
          }
        };

        document.addEventListener('mouseleave', handleMouseLeave);
        // Clean up the event listener inside the effect cleanup
        return () => document.removeEventListener('mouseleave', handleMouseLeave);
      }
    };

    loadSettings();
  }, [hasShown]);

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();

    if (!formData.name || !formData.phone) {
      toast.error('Please fill in required fields');
      return;
    }

    const leadData = {
      ...formData,
      source: 'Exit Intent Popup',
      status: 'New',
      createdAt: new Date().toISOString()
    };

    await addItem('leads', leadData);
    toast.success('Thank you! We will contact you soon.');
    setIsVisible(false);

    // Redirect to WhatsApp if enabled
    if (settings.redirectToWhatsApp) {
      const websiteSettings = await getData('settings');
      // Handle potential array response or null
      let whatsappNumber = '';
      if (websiteSettings && !Array.isArray(websiteSettings)) {
        whatsappNumber = websiteSettings.contact?.phone?.replace(/[^0-9]/g, '') || '';
      }

      if (whatsappNumber) {
        const message = `Hi, I'm interested in your projects. My name is ${formData.name}. Please share more details.`;
        window.open(`https://wa.me/${whatsappNumber}?text=${encodeURIComponent(message)}`, '_blank');
      }
    }
  };

  if (!isVisible || !settings) {
    return null;
  }

  return (
    <div className="fixed inset-0 z-50 flex items-center justify-center bg-black/50 animate-in fade-in duration-300">
      <div className="bg-white rounded-xl shadow-2xl max-w-md w-full mx-4 animate-in slide-in-from-bottom-4 duration-300">
        {/* Header */}
        <div
          className="p-6 rounded-t-xl text-white relative overflow-hidden"
          style={{
            background: `linear-gradient(135deg, ${colors.primaryColor} 0%, ${colors.secondaryColor} 100%)`
          }}
        >
          <button
            onClick={() => setIsVisible(false)}
            className="absolute top-4 right-4 text-white/80 hover:text-white transition-colors"
          >
            <X className="h-5 w-5" />
          </button>

          <div className="relative z-10">
            <h2 className="text-2xl mb-2">{settings.title || "Wait! Don't Miss Out"}</h2>
            <p className="text-white/90 text-sm">
              {settings.description || "Get exclusive access to our latest projects and special offers"}
            </p>
          </div>
        </div>

        {/* Content */}
        <div className="p-6">
          <form onSubmit={handleSubmit} className="space-y-4">
            <div>
              <Label htmlFor="popup-name">Your Name *</Label>
              <Input
                id="popup-name"
                value={formData.name}
                onChange={(e) => setFormData({ ...formData, name: e.target.value })}
                placeholder="Enter your name"
                required
              />
            </div>

            <div>
              <Label htmlFor="popup-phone">Phone Number *</Label>
              <Input
                id="popup-phone"
                type="tel"
                value={formData.phone}
                onChange={(e) => setFormData({ ...formData, phone: e.target.value })}
                placeholder="+91 XXXXX XXXXX"
                required
              />
            </div>

            <div>
              <Label htmlFor="popup-email">Email (Optional)</Label>
              <Input
                id="popup-email"
                type="email"
                value={formData.email}
                onChange={(e) => setFormData({ ...formData, email: e.target.value })}
                placeholder="your.email@example.com"
              />
            </div>

            <Button
              type="submit"
              className="w-full text-white"
              style={{ backgroundColor: colors.primaryColor }}
            >
              {settings.ctaText || "Get Instant Access"}
            </Button>
          </form>

          {settings.showBenefits && (
            <div className="mt-6 space-y-2 text-sm">
              <p className="font-medium text-slate-900">You'll Get:</p>
              <ul className="space-y-2">
                <li className="flex items-center gap-2 text-slate-600">
                  <Download className="h-4 w-4" style={{ color: colors.primaryColor }} />
                  <span>Exclusive project brochures</span>
                </li>
                <li className="flex items-center gap-2 text-slate-600">
                  <Phone className="h-4 w-4" style={{ color: colors.primaryColor }} />
                  <span>Priority site visit booking</span>
                </li>
                <li className="flex items-center gap-2 text-slate-600">
                  <Mail className="h-4 w-4" style={{ color: colors.primaryColor }} />
                  <span>Early bird special offers</span>
                </li>
              </ul>
            </div>
          )}
        </div>
      </div>
    </div>
  );
}

import { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import { getData } from '../../utils/localStorage';
import { Mail, Phone, MapPin, Facebook, Twitter, Linkedin, Instagram, ArrowRight } from 'lucide-react';
import { Button } from '../ui/button';
import { useTheme } from '../../contexts/ThemeContext';

export function Footer() {
  const { colors } = useTheme();
  const [settings, setSettings] = useState<any>(null);
  const [contact, setContact] = useState<any>(null);
  const [menus, setMenus] = useState<any[]>([]);

  useEffect(() => {
    const loadData = async () => {
      const websiteSettings = await getData('settings');
      if (websiteSettings && !Array.isArray(websiteSettings)) {
        // Handle potential nested 'settings' key
        const actualSettings = websiteSettings.settings || websiteSettings;
        setSettings(actualSettings);
      }

      const cmsPages = await getData('cmsPages');
      if (cmsPages && !Array.isArray(cmsPages)) {
        setContact(cmsPages.contact);
      }

      const menuData = await getData('menus');
      if (menuData && !Array.isArray(menuData)) {
        setMenus(menuData.footer || []);
      }
    };
    loadData();
  }, []);

  if (!settings) return null;

  return (
    <footer className="bg-gradient-to-br from-slate-900 via-slate-800 to-slate-900 text-white mt-auto">
      {/* Newsletter Section */}
      <div className="border-b border-slate-700">
        <div className="container py-12">
          <div className="flex flex-col md:flex-row items-center justify-between gap-6">
            <div>
              <h3 className="text-2xl mb-2">Stay Updated</h3>
              <p className="text-slate-400">Get the latest updates on new projects and offers</p>
            </div>
            <div className="flex gap-3 w-full md:w-auto">
              <input
                type="email"
                placeholder="Enter your email"
                className="px-4 py-3 rounded-lg bg-slate-800 border border-slate-700 focus:outline-none text-white placeholder:text-slate-500 flex-1 md:w-80"
                style={{
                  borderColor: '#334155'
                }}
                onFocus={(e) => {
                  e.currentTarget.style.borderColor = colors.primaryColor;
                }}
                onBlur={(e) => {
                  e.currentTarget.style.borderColor = '#334155';
                }}
              />
              <Button
                className="text-white px-6"
                style={{ backgroundColor: colors.primaryColor }}
                onMouseEnter={(e) => e.currentTarget.style.opacity = '0.9'}
                onMouseLeave={(e) => e.currentTarget.style.opacity = '1'}
              >
                Subscribe
              </Button>
            </div>
          </div>
        </div>
      </div>

      <div className="container py-16">
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-12">
          {/* About */}
          <div className="lg:col-span-1">
            <div className="mb-6">
              {settings.website.footerLogo ? (
                <img src={settings.website.footerLogo} alt={settings.website.name} className="h-12 mb-4" />
              ) : (
                <h3 className="text-2xl mb-3">{settings.website.name}</h3>
              )}
            </div>
            <p className="text-slate-400 leading-relaxed mb-6">
              {settings.footer?.text || settings.website.slogan || 'Building dreams, creating landmarks. Your trusted partner in real estate development.'}
            </p>

            {/* Social Media */}
            <div className="flex items-center gap-3">
              {settings.social?.facebook && (
                <a
                  href={settings.social.facebook}
                  target="_blank"
                  rel="noopener noreferrer"
                  className="h-10 w-10 rounded-lg bg-slate-800 flex items-center justify-center transition-all hover:scale-110"
                  style={{ backgroundColor: '#1e293b' }}
                  onMouseEnter={(e) => e.currentTarget.style.backgroundColor = colors.primaryColor}
                  onMouseLeave={(e) => e.currentTarget.style.backgroundColor = '#1e293b'}
                >
                  <Facebook className="h-4 w-4" />
