import { useState, useEffect } from 'react';
import { AdminLayout } from '../../components/admin/AdminLayout';
import { Card } from '../../components/ui/card';
import { Button } from '../../components/ui/button';
import { Input } from '../../components/ui/input';
import { Label } from '../../components/ui/label';
import { Textarea } from '../../components/ui/textarea';
import { Switch } from '../../components/ui/switch';
import { getData, setData } from '../../utils/localStorage';
import { toast } from 'sonner@2.0.3';
import { MessageSquare, Save } from 'lucide-react';

export function WhatsAppWidget() {
  const [settings, setSettings] = useState({
    enabled: true,
    phoneNumber: '',
    welcomeMessage: 'Hello! How can we help you today?',
    position: 'bottom-right',
    backgroundColor: '#25D366',
    buttonText: 'Chat with us',
    showOnPages: {
      home: true,
      projects: true,
      projectDetail: true,
      about: true,
      contact: true,
      gallery: true,
      blog: true
    },
    offlineMessage: 'We are currently offline. Please leave a message and we\'ll get back to you soon.',
    businessHours: {
      enabled: false,
      start: '09:00',
      end: '18:00',
      days: ['Monday', 'Tuesday', 'Wednesday', 'Thursday', 'Friday', 'Saturday']
    }
  });

  useEffect(() => {
    const savedSettings = getData('whatsappWidgetSettings');
    if (savedSettings) {
      setSettings(savedSettings);
    }
  }, []);

  const handleSave = () => {
    setData('whatsappWidgetSettings', settings);
    toast.success('WhatsApp Widget settings saved successfully!');
  };

  return (
    <AdminLayout>
      <div className="max-w-4xl mx-auto space-y-6">
        <div className="flex items-center justify-between">
          <div>
            <h1 className="text-3xl mb-2">WhatsApp Chat Widget</h1>
            <p className="text-slate-600">Configure floating WhatsApp chat button for your website</p>
          </div>
          <Button onClick={handleSave}>
            <Save className="h-4 w-4 mr-2" />
            Save Settings
          </Button>
        </div>

        {/* Basic Settings */}
        <Card className="p-6">
          <h2 className="text-xl mb-4 flex items-center gap-2">
            <MessageSquare className="h-5 w-5" />
            Basic Settings
          </h2>

          <div className="space-y-4">
            <div className="flex items-center justify-between">
              <div>
                <Label>Enable WhatsApp Widget</Label>
                <p className="text-sm text-slate-500">Show floating WhatsApp button on website</p>
              </div>
              <Switch
                checked={settings.enabled}
                onCheckedChange={(checked) => setSettings({ ...settings, enabled: checked })}
              />
            </div>

            <div>
              <Label htmlFor="phoneNumber">WhatsApp Phone Number *</Label>
              <Input
                id="phoneNumber"
                value={settings.phoneNumber}
                onChange={(e) => setSettings({ ...settings, phoneNumber: e.target.value })}
                placeholder="+91 9876543210"
              />
              <p className="text-xs text-slate-500 mt-1">Include country code (e.g., +91 for India)</p>
            </div>

            <div>
              <Label htmlFor="welcomeMessage">Welcome Message</Label>
              <Textarea
                id="welcomeMessage"
                value={settings.welcomeMessage}
                onChange={(e) => setSettings({ ...settings, welcomeMessage: e.target.value })}
                placeholder="Hello! How can we help you today?"
                rows={3}
              />
            </div>

            <div>
              <Label htmlFor="buttonText">Button Text</Label>
              <Input
                id="buttonText"
                value={settings.buttonText}
                onChange={(e) => setSettings({ ...settings, buttonText: e.target.value })}
                placeholder="Chat with us"
              />
            </div>

            <div className="grid grid-cols-2 gap-4">
              <div>
                <Label htmlFor="position">Position</Label>
                <select
                  id="position"
                  value={settings.position}
                  onChange={(e) => setSettings({ ...settings, position: e.target.value })}
                  className="w-full border rounded-lg px-3 py-2"
                >
                  <option value="bottom-right">Bottom Right</option>
                  <option value="bottom-left">Bottom Left</option>
                </select>
              </div>

              <div>
                <Label htmlFor="backgroundColor">Button Color</Label>
                <Input
                  id="backgroundColor"
                  type="color"
                  value={settings.backgroundColor}
                  onChange={(e) => setSettings({ ...settings, backgroundColor: e.target.value })}
                />
              </div>
            </div>
          </div>
        </Card>

        {/* Display Pages */}
        <Card className="p-6">
          <h2 className="text-xl mb-4">Show Widget On Pages</h2>
          <div className="grid grid-cols-2 gap-4">
            {Object.keys(settings.showOnPages).map((page) => (
              <div key={page} className="flex items-center justify-between p-3 bg-slate-50 rounded-lg">
                <Label className="capitalize">{page.replace(/([A-Z])/g, ' $1').trim()}</Label>
                <Switch
                  checked={settings.showOnPages[page as keyof typeof settings.showOnPages]}
                  onCheckedChange={(checked) => setSettings({
                    ...settings,
                    showOnPages: { ...settings.showOnPages, [page]: checked }
                  })}
                />
              </div>
            ))}
          </div>
        </Card>

        {/* Business Hours */}
        <Card className="p-6">
          <h2 className="text-xl mb-4">Business Hours (Optional)</h2>

          <div className="space-y-4">
            <div className="flex items-center justify-between">
              <div>
                <Label>Enable Business Hours</Label>
                <p className="text-sm text-slate-500">Show offline message outside business hours</p>
              </div>
              <Switch
                checked={settings.businessHours.enabled}
                onCheckedChange={(checked) => setSettings({
                  ...settings,
                  businessHours: { ...settings.businessHours, enabled: checked }
                })}
              />
            </div>

            {settings.businessHours.enabled && (
              <>
                <div className="grid grid-cols-2 gap-4">
                  <div>
                    <Label htmlFor="startTime">Start Time</Label>
                    <Input
                      id="startTime"
                      type="time"
                      value={settings.businessHours.start}
                      onChange={(e) => setSettings({
                        ...settings,
                        businessHours: { ...settings.businessHours, start: e.target.value }
                      })}
                    />
                  </div>
                  <div>
                    <Label htmlFor="endTime">End Time</Label>
                    <Input
                      id="endTime"
                      type="time"
                      value={settings.businessHours.end}
                      onChange={(e) => setSettings({
                        ...settings,
                        businessHours: { ...settings.businessHours, end: e.target.value }
                      })}
                    />
                  </div>
                </div>

                <div>
                  <Label>Working Days</Label>
                  <div className="grid grid-cols-4 gap-2 mt-2">
                    {['Monday', 'Tuesday', 'Wednesday', 'Thursday', 'Friday', 'Saturday', 'Sunday'].map((day) => (
                      <div key={day} className="flex items-center gap-2">
                        <input
                          type="checkbox"
                          id={day}
                          checked={settings.businessHours.days.includes(day)}
                          onChange={(e) => {
                            const days = e.target.checked
                              ? [...settings.businessHours.days, day]
                              : settings.businessHours.days.filter(d => d !== day);
                            setSettings({
                              ...settings,
                              businessHours: { ...settings.businessHours, days }
                            });
                          }}
                          className="rounded"
                        />
                        <Label htmlFor={day} className="text-sm">{day.slice(0, 3)}</Label>
                      </div>
                    ))}
                  </div>
                </div>

                <div>
                  <Label htmlFor="offlineMessage">Offline Message</Label>
                  <Textarea
                    id="offlineMessage"
                    value={settings.offlineMessage}
                    onChange={(e) => setSettings({ ...settings, offlineMessage: e.target.value })}
                    rows={2}
                  />
                </div>
              </>
            )}
          </div>
        </Card>

        <div className="flex justify-end">
          <Button onClick={handleSave} size="lg">
            <Save className="h-4 w-4 mr-2" />
            Save All Settings
          </Button>
        </div>
      </div>
    </AdminLayout>
  );
}

import { useEffect, useState } from 'react';
import { getData } from '../../utils/localStorage';
import { Header } from '../../components/frontend/Header';
import { Footer } from '../../components/frontend/Footer';
import { Target, Eye } from 'lucide-react';
import { useTheme } from '../../contexts/ThemeContext';

export function About() {
  const { colors } = useTheme();
  const [aboutData, setAboutData] = useState<any>(null);

  useEffect(() => {
    const loadData = async () => {
      const cmsPages = await getData('cmsPages');
      if (cmsPages?.about) {
        setAboutData(cmsPages.about);
      }
    };
    loadData();
  }, []);

  if (!aboutData) return null;

  return (
    <div className="min-h-screen flex flex-col">
      <Header />

      <div className="flex-1">
        {/* Page Header */}
        <div
          className="text-white py-16"
          style={{ background: `linear-gradient(135deg, ${colors.primaryColor}, ${colors.secondaryColor})` }}
        >
          <div className="container mx-auto px-4">
            <h1 className="text-4xl mb-4">{aboutData.title}</h1>
          </div>
        </div>

        <div className="container mx-auto px-4 py-12">
          <div className="grid grid-cols-1 lg:grid-cols-2 gap-12 items-center mb-12">
            <div>
              <div
                className="prose max-w-none"
                dangerouslySetInnerHTML={{ __html: aboutData.content }}
              />
            </div>

            {aboutData.image && (
              <div>
                <img
                  src={aboutData.image}
                  alt="About Us"
                  className="w-full rounded-lg shadow-lg"
                />
              </div>
            )}
          </div>

          {/* Vision & Mission */}
          <div className="grid grid-cols-1 md:grid-cols-2 gap-8 mt-12">
            <div className="bg-white p-8 rounded-lg shadow-lg">
              <Eye className="h-12 w-12 mb-4" style={{ color: colors.primaryColor }} />
              <h3 className="text-2xl mb-4" style={{ color: colors.textColor }}>Our Vision</h3>
              <p className="text-gray-600">{aboutData.vision}</p>
            </div>

            <div className="bg-white p-8 rounded-lg shadow-lg">
              <Target className="h-12 w-12 mb-4" style={{ color: colors.accentColor }} />
              <h3 className="text-2xl mb-4" style={{ color: colors.textColor }}>Our Mission</h3>
              <p className="text-gray-600">{aboutData.mission}</p>
            </div>
          </div>
        </div>
      </div>

      <Footer />
    </div>
  );
}

import { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import { getData } from '../../utils/localStorage';
import { Header } from '../../components/frontend/Header';
import { Footer } from '../../components/frontend/Footer';
import { Calendar, User, FileText } from 'lucide-react';
import { useTheme } from '../../contexts/ThemeContext';

export function BlogList() {
  const [posts, setPosts] = useState<any[]>([]);
  const [categories, setCategories] = useState<any[]>([]);
  const [selectedCategory, setSelectedCategory] = useState('all');
  const { colors } = useTheme();

  useEffect(() => {
    const loadData = async () => {
      const allPosts = await getData('blogPosts') || [];
      setPosts(allPosts.filter((post: any) => post.status === 'Published'));
      const categoriesData = await getData('blogCategories') || [];
      setCategories(categoriesData);
    };
    loadData();
  }, []);

  const filteredPosts = selectedCategory === 'all'
    ? posts
    : posts.filter(post => post.categoryId === selectedCategory);

  return (
    <div className="min-h-screen flex flex-col">
      <Header />

      <div className="flex-1">
        {/* Page Header */}
        <div
          className="text-white py-16"
          style={{ background: `linear-gradient(to right, ${colors.primaryColor}, ${colors.secondaryColor})` }}
        >
          <div className="container mx-auto px-4">
            <h1 className="text-4xl mb-4">Blog</h1>
            <p className="text-xl">Latest news and updates</p>
          </div>
        </div>

        <div className="container mx-auto px-4 py-12">
          <div className="grid grid-cols-1 lg:grid-cols-4 gap-8">
            {/* Sidebar */}
            <div className="lg:col-span-1">
              <div className="bg-white p-6 rounded-lg shadow-lg sticky top-24">
                <h3
                  className="text-xl mb-4"
                  style={{ color: colors.textColor }}
                >
                  Categories
                </h3>
                <div className="space-y-2">
                  <button
                    onClick={() => setSelectedCategory('all')}
                    className={`w-full text-left px-4 py-2 rounded transition-colors ${selectedCategory === 'all'
                        ? 'text-white'
                        : 'hover:bg-gray-100'
                      }`}
                    style={selectedCategory === 'all' ? { backgroundColor: colors.primaryColor } : {}}
                  >
                    All Posts
                  </button>
                  {categories.map(category => (
                    <button
                      key={category.id}
                      onClick={() => setSelectedCategory(category.id)}
                      className={`w-full text-left px-4 py-2 rounded transition-colors ${selectedCategory === category.id
                          ? 'text-white'
                          : 'hover:bg-gray-100'
                        }`}
                      style={selectedCategory === category.id ? { backgroundColor: colors.primaryColor } : {}}
                    >
                      {category.name}
                    </button>
                  ))}
                </div>
              </div>
            </div>

            {/* Posts Grid */}
            <div className="lg:col-span-3">
              {filteredPosts.length > 0 ? (
                <div className="grid grid-cols-1 md:grid-cols-2 gap-8">
                  {filteredPosts.map((post) => (
                    <Link
                      key={post.id}
                      to={`/blog/${post.slug}`}
                      className="bg-white rounded-lg shadow-lg overflow-hidden hover:shadow-xl transition-all group"
                      style={{
                        '--hover-border-color': colors.accentColor
                      } as React.CSSProperties}
                      onMouseEnter={(e) => {
                        e.currentTarget.style.borderColor = colors.accentColor;
                        e.currentTarget.style.borderWidth = '2px';
                      }}
                      onMouseLeave={(e) => {
                        e.currentTarget.style.borderColor = 'transparent';
                        e.currentTarget.style.borderWidth = '0px';
                      }}
                    >
                      {post.featuredImage ? (
                        <img
                          src={post.featuredImage}
                          alt={post.title}
                          className="w-full h-48 object-cover"
                        />
                      ) : (
                        <div
                          className="w-full h-48 flex items-center justify-center"
                          style={{ background: `linear-gradient(to bottom right, ${colors.primaryColor}, ${colors.secondaryColor})` }}
                        >
                          <FileText className="h-16 w-16 text-white opacity-50" />
                        </div>
                      )}
                      <div className="p-6">
                        <div className="flex items-center gap-4 text-sm text-gray-600 mb-3">
                          <div className="flex items-center gap-1">
                            <Calendar className="h-4 w-4" />
                            <span>{new Date(post.publishDate).toLocaleDateString()}</span>
                          </div>
                          <div className="flex items-center gap-1">
                            <User className="h-4 w-4" />
                            <span>{post.author}</span>
                          </div>
                        </div>
                        <h3
                          className="text-xl mb-2"
                          style={{ color: colors.textColor }}
                        >
                          {post.title}
                        </h3>
                        {post.metaDescription && (
                          <p className="text-gray-600 text-sm line-clamp-3">
                            {post.metaDescription}
                          </p>
                        )}
                        <div className="mt-4">
                          <span
                            className="hover:underline transition-colors"
                            style={{ color: colors.primaryColor }}
                          >
                            Read More â
                          </span>
                        </div>
                      </div>
                    </Link>
                  ))}
                </div>
              ) : (
                <div className="text-center py-12 text-gray-500">
                  <FileText className="h-16 w-16 mx-auto mb-4 opacity-50" />
                  <p>No blog posts found</p>
                </div>
              )}
            </div>
          </div>
        </div>
      </div>

      <Footer />
    </div>
  );
}

import { useEffect, useState } from 'react';
import { useParams, Link, useNavigate } from 'react-router-dom';
import { getData } from '../../utils/localStorage';
import { Header } from '../../components/frontend/Header';
import { Footer } from '../../components/frontend/Footer';
import { Calendar, User, ArrowLeft, Clock, Share2, Tag, ChevronRight, Facebook, Twitter, Linkedin, Link as LinkIcon, MessageCircle } from 'lucide-react';
import { useTheme } from '../../contexts/ThemeContext';

export function BlogPost() {
  const { slug } = useParams();
  const navigate = useNavigate();
  const [post, setPost] = useState<any>(null);
  const [category, setCategory] = useState<any>(null);
  const [relatedPosts, setRelatedPosts] = useState<any[]>([]);
  const [allPosts, setAllPosts] = useState<any[]>([]);
  const { colors } = useTheme();

  useEffect(() => {
    const loadData = async () => {
      const posts = await getData('blogPosts') || [];
      const foundPost = posts.find((p: any) => p.slug === slug);
      setPost(foundPost);

      if (foundPost) {
        const categories = await getData('blogCategories') || [];
        setCategory(categories.find((c: any) => c.id === foundPost.categoryId));

        // Get related posts
        const related = posts
          .filter((p: any) => p.categoryId === foundPost.categoryId && p.id !== foundPost.id && p.status === 'Published')
          .slice(0, 3);
        setRelatedPosts(related);
      }
    };
    loadData();
  }, [slug]);

  if (!post) {
    return (
      <div className="min-h-screen flex flex-col">
        <Header />
        <div className="flex-1 flex items-center justify-center">
          <div className="text-center">
            <h2 className="text-2xl mb-4">Post not found</h2>
            <Link to="/blog" className="text-blue-600 hover:underline">
              â Back to Blog
            </Link>
          </div>
        </div>
        <Footer />
      </div>
    );
  }

  const readingTime = Math.ceil((post.content?.length || 0) / 1000);

  return (
    <div className="min-h-screen flex flex-col bg-slate-50">
      <Header />

      <div className="flex-1">
        {/* Simple Header Section */}
        <div
          className="relative py-8 md:py-12"
          style={{ background: `linear-gradient(135deg, ${colors.primaryColor}, ${colors.secondaryColor})` }}
        >
          <div className="absolute inset-0 bg-black/10" />

          {/* Hero Content */}
          <div className="relative container mx-auto px-4">
            <div className="max-w-4xl mx-auto">
              <Link
                to="/blog"
                className="inline-flex items-center gap-2 text-white/90 hover:text-white mb-4 transition-colors text-sm"
              >
                <ArrowLeft className="h-4 w-4" />
                Back to Blog
              </Link>

              {category && (
                <div className="mb-3">
                  <span
                    className="inline-block px-3 py-1 rounded-full text-xs font-medium bg-white/20 backdrop-blur-sm text-white"
                  >
                    {category.name}
                  </span>
                </div>
              )}

              <h1 className="text-3xl md:text-4xl text-white mb-4 leading-tight">
                {post.title}
              </h1>

              <div className="flex flex-wrap items-center gap-4 text-sm text-white/90">
                <div className="flex items-center gap-2">
                  <User className="h-4 w-4" />
                  <span>{post.author}</span>
                </div>
                <div className="flex items-center gap-2">
                  <Calendar className="h-4 w-4" />
                  <span>{new Date(post.publishDate).toLocaleDateString('en-US', {
                    year: 'numeric',
                    month: 'long',
                    day: 'numeric'
                  })}</span>
                </div>
                <div className="flex items-center gap-2">
                  <Clock className="h-4 w-4" />
                  <span>{readingTime} min read</span>
                </div>
              </div>
            </div>
          </div>
        </div>

        {/* Main Content Area */}
        <div className="container mx-auto px-4 py-12">
          <div className="grid grid-cols-1 lg:grid-cols-12 gap-8 max-w-7xl mx-auto">
            {/* Main Content */}
            <div className="lg:col-span-8">
              <article className="bg-white rounded-lg shadow-lg p-8 md:p-12">
                {/* Featured Image at Content Start */}
                {post.featuredImage && (
                  <div className="mb-8 -mx-8 md:-mx-12 -mt-8 md:-mt-12">
                    <img
                      src={post.featuredImage}
                      alt={post.title}
                      className="w-full h-[400px] object-cover rounded-t-lg"
                    />
                  </div>
                )}

                {/* Meta Description */}
                {post.metaDescription && (
                  <div className="mb-8 pb-8 border-b">
                    <p className="text-lg text-gray-700 leading-relaxed italic">
                      {post.metaDescription}
                    </p>
                  </div>
                )}

                {/* Post Content */}
                <div
                  className="prose prose-lg max-w-none"
                  dangerouslySetInnerHTML={{ __html: post.content }}
                  style={{
                    '--tw-prose-headings': colors.textColor,
                    '--tw-prose-links': colors.primaryColor,
                    '--tw-prose-bold': colors.textColor,
                  } as React.CSSProperties}
                />

                {/* Share Section */}
                <div className="mt-12 pt-8 border-t">
                  <div className="mb-4">
                    <div className="flex items-center gap-2 text-gray-900 mb-4">
                      <Share2 className="h-5 w-5" style={{ color: colors.primaryColor }} />
                      <h3 className="text-lg font-medium">Share this article</h3>
                    </div>
                    <p className="text-sm text-gray-600 mb-6">Help others discover this content by sharing it on your favorite platform</p>
                  </div>

                  <div className="flex flex-wrap gap-3">
                    {/* Facebook */}
                    <a
                      href={`https://www.facebook.com/sharer/sharer.php?u=${encodeURIComponent(window.location.href)}`}
                      target="_blank"
                      rel="noopener noreferrer"
                      className="flex items-center gap-2 px-4 py-2.5 bg-[#1877F2] text-white rounded-lg hover:bg-[#1666d9] transition-all shadow-md hover:shadow-lg"
                    >
                      <Facebook className="h-4 w-4" />
                      <span className="text-sm font-medium">Facebook</span>
                    </a>

                    {/* Twitter */}
                    <a
                      href={`https://twitter.com/intent/tweet?url=${encodeURIComponent(window.location.href)}&text=${encodeURIComponent(post.title)}`}
                      target="_blank"
                      rel="noopener noreferrer"
                      className="flex items-center gap-2 px-4 py-2.5 bg-[#1DA1F2] text-white rounded-lg hover:bg-[#1a8cd8] transition-all shadow-md hover:shadow-lg"
                    >
                      <Twitter className="h-4 w-4" />
                      <span className="text-sm font-medium">Twitter</span>
                    </a>

                    {/* LinkedIn */}
                    <a
                      href={`https://www.linkedin.com/shareArticle?mini=true&url=${encodeURIComponent(window.location.href)}&title=${encodeURIComponent(post.title)}`}
                      target="_blank"
                      rel="noopener noreferrer"
                      className="flex items-center gap-2 px-4 py-2.5 bg-[#0A66C2] text-white rounded-lg hover:bg-[#095196] transition-all shadow-md hover:shadow-lg"
                    >
                      <Linkedin className="h-4 w-4" />
                      <span className="text-sm font-medium">LinkedIn</span>
                    </a>

                    {/* WhatsApp */}
                    <a
                      href={`https://wa.me/?text=${encodeURIComponent(post.title + ' - ' + window.location.href)}`}
                      target="_blank"
                      rel="noopener noreferrer"
                      className="flex items-center gap-2 px-4 py-2.5 bg-[#25D366] text-white rounded-lg hover:bg-[#20bd5a] transition-all shadow-md hover:shadow-lg"
                    >
                      <MessageCircle className="h-4 w-4" />
                      <span className="text-sm font-medium">WhatsApp</span>
                    </a>

                    {/* Copy Link */}
                    <button
                      onClick={() => {
                        navigator.clipboard.writeText(window.location.href);
                        const btn = document.getElementById('copy-link-btn');
                        if (btn) {
                          const originalText = btn.textContent;
                          btn.textContent = 'Copied!';
                          setTimeout(() => {
                            btn.textContent = originalText || 'Copy Link';
                          }, 2000);
                        }
                      }}
                      className="flex items-center gap-2 px-4 py-2.5 bg-gray-700 text-white rounded-lg hover:bg-gray-800 transition-all shadow-md hover:shadow-lg"
                    >
                      <LinkIcon className="h-4 w-4" />
                      <span id="copy-link-btn" className="text-sm font-medium">Copy Link</span>
                    </button>
                  </div>
                </div>

                {/* Author Info Card */}
                <div className="mt-8 p-6 bg-slate-50 rounded-lg">
                  <div className="flex items-center gap-4">
                    <div
                      className="h-16 w-16 rounded-full flex items-center justify-center text-white text-xl"
                      style={{ backgroundColor: colors.primaryColor }}
                    >
                      {post.author.charAt(0).toUpperCase()}
                    </div>
                    <div>
                      <h3 className="text-lg font-medium">{post.author}</h3>
                      <p className="text-gray-600">Article Author</p>
                    </div>
                  </div>
                </div>
              </article>
            </div>

            {/* Sidebar */}
            <div className="lg:col-span-4">
              <div className="sticky top-24 space-y-6">
                {/* Category Card */}
                {category && (
                  <div className="bg-white rounded-lg shadow-lg p-6">
                    <div className="flex items-center gap-2 mb-4">
                      <Tag className="h-5 w-5" style={{ color: colors.primaryColor }} />
                      <h3 className="text-lg font-medium">Category</h3>
                    </div>
                    <Link
                      to="/blog"
                      className="inline-block px-4 py-2 rounded-lg transition-colors hover:shadow-md"
                      style={{
                        backgroundColor: `${colors.primaryColor}10`,
                        color: colors.primaryColor
                      }}
                    >
                      {category.name}
                    </Link>
                  </div>
                )}

                {/* Recent Posts */}
                {allPosts.length > 1 && (
                  <div className="bg-white rounded-lg shadow-lg p-6">
                    <h3 className="text-lg font-medium mb-4">Recent Posts</h3>
                    <div className="space-y-4">
                      {allPosts
                        .filter(p => p.id !== post.id)
                        .slice(0, 5)
                        .map((recentPost) => (
                          <Link
                            key={recentPost.id}
                            to={`/blog/${recentPost.slug}`}
                            className="block group"
                          >
                            <h4 className="text-sm font-medium group-hover:underline transition-colors line-clamp-2"
                              style={{ color: colors.textColor }}
                            >
                              {recentPost.title}
                            </h4>
                            <p className="text-xs text-gray-500 mt-1">
                              {new Date(recentPost.publishDate).toLocaleDateString()}
                            </p>
                          </Link>
                        ))}
                    </div>
                  </div>
                )}
              </div>
            </div>
          </div>

          {/* Related Posts Section */}
          {relatedPosts.length > 0 && (
            <div className="max-w-7xl mx-auto mt-16">
              <div className="mb-8">
                <h2 className="text-3xl mb-2" style={{ color: colors.textColor }}>
                  Related Articles
                </h2>
                <p className="text-gray-600">
                  More posts from {category?.name}
                </p>
              </div>

              <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
                {relatedPosts.map((relatedPost) => (
                  <Link
                    key={relatedPost.id}
                    to={`/blog/${relatedPost.slug}`}
                    className="bg-white rounded-lg shadow-lg overflow-hidden hover:shadow-xl transition-all group"
                  >
                    {/* Image */}
                    {relatedPost.featuredImage ? (
                      <img
                        src={relatedPost.featuredImage}
                        alt={relatedPost.title}
                        className="w-full h-48 object-cover group-hover:scale-105 transition-transform duration-300"
                      />
                    ) : (
                      <div
                        className="w-full h-48 flex items-center justify-center"
                        style={{ background: `linear-gradient(135deg, ${colors.primaryColor}, ${colors.secondaryColor})` }}
                      >
                        <Tag className="h-12 w-12 text-white opacity-50" />
                      </div>
                    )}

                    {/* Content */}
                    <div className="p-6">
                      <div className="flex items-center gap-2 text-xs text-gray-500 mb-3">
                        <Calendar className="h-3 w-3" />
                        <span>
                          {new Date(relatedPost.publishDate).toLocaleDateString()}
                        </span>
                      </div>

                      <h3
                        className="text-xl mb-3 line-clamp-2 group-hover:underline"
                        style={{ color: colors.textColor }}
                      >
                        {relatedPost.title}
                      </h3>

                      {relatedPost.metaDescription && (
                        <p className="text-sm text-gray-600 line-clamp-2 mb-4">
                          {relatedPost.metaDescription}
                        </p>
                      )}

                      <div
                        className="inline-flex items-center gap-1 text-sm font-medium group-hover:gap-2 transition-all"
                        style={{ color: colors.primaryColor }}
                      >
                        Read More
                        <ChevronRight className="h-4 w-4" />
                      </div>
                    </div>
                  </Link>
                ))}
              </div>
            </div>
          )}

          {/* Navigation to Previous/Next Post */}
          <div className="max-w-7xl mx-auto mt-12 grid grid-cols-1 md:grid-cols-2 gap-6">
            {allPosts.findIndex(p => p.id === post.id) > 0 && (
              <button
                onClick={() => {
                  const prevPost = allPosts[allPosts.findIndex(p => p.id === post.id) - 1];
                  navigate(`/blog/${prevPost.slug}`);
                }}
                className="bg-white rounded-lg shadow-lg p-6 text-left hover:shadow-xl transition-all group"
              >
                <div className="flex items-center gap-2 text-sm text-gray-500 mb-2">
                  <ArrowLeft className="h-4 w-4" />
                  <span>Previous Article</span>
                </div>
                <h3
                  className="text-lg font-medium group-hover:underline"
                  style={{ color: colors.textColor }}
                >
                  {allPosts[allPosts.findIndex(p => p.id === post.id) - 1]?.title}
                </h3>
              </button>
            )}

            {allPosts.findIndex(p => p.id === post.id) < allPosts.length - 1 && (
              <button
                onClick={() => {
                  const nextPost = allPosts[allPosts.findIndex(p => p.id === post.id) + 1];
                  navigate(`/blog/${nextPost.slug}`);
                }}
                className="bg-white rounded-lg shadow-lg p-6 text-right hover:shadow-xl transition-all group"
              >
                <div className="flex items-center justify-end gap-2 text-sm text-gray-500 mb-2">
                  <span>Next Article</span>
                  <ChevronRight className="h-4 w-4" />
                </div>
                <h3
                  className="text-lg font-medium group-hover:underline"
                  style={{ color: colors.textColor }}
                >
                  {allPosts[allPosts.findIndex(p => p.id === post.id) + 1]?.title}
                </h3>
              </button>
            )}
          </div>
        </div>
      </div>

      <Footer />
    </div>
  );
}

import { useEffect, useState } from 'react';
import { getData, addItem } from '../../utils/localStorage';
import { Header } from '../../components/frontend/Header';
import { Footer } from '../../components/frontend/Footer';
import { Button } from '../../components/ui/button';
import { Input } from '../../components/ui/input';
import { Label } from '../../components/ui/label';
import { Textarea } from '../../components/ui/textarea';
import { toast } from 'sonner';
import { Mail, Phone, MapPin, Clock, Send, Building2, MessageSquare } from 'lucide-react';
import { useTheme } from '../../contexts/ThemeContext';

export function Contact() {
  const { colors } = useTheme();
  const [contactData, setContactData] = useState<any>(null);
  const [projects, setProjects] = useState<any[]>([]);
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    phone: '',
    projectInterest: '',
    message: ''
  });

  useEffect(() => {
    const loadData = async () => {
      const cmsPages = await getData('cmsPages');
      if (cmsPages?.contact) {
        setContactData(cmsPages.contact);
      }
      const projectsData = await getData('projects');
      setProjects(projectsData || []);
    };
    loadData();
  }, []);

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();

    if (!formData.name || !formData.email || !formData.phone) {
      toast.error('Please fill in all required fields');
      return;
    }

    addItem('leads', {
      ...formData,
      status: 'New',
      source: 'Website'
    });

    toast.success('Thank you! We will contact you soon.');
    setFormData({
      name: '',
      email: '',
      phone: '',
      projectInterest: '',
      message: ''
    });
  };

  if (!contactData) return null;

  return (
    <div className="min-h-screen flex flex-col bg-slate-50">
      <Header />

      <div className="flex-1">
        {/* Page Header */}
        <div
          className="text-white py-20 relative overflow-hidden"
          style={{ background: `linear-gradient(135deg, ${colors.primaryColor}, ${colors.secondaryColor})` }}
        >
          <div className="absolute inset-0 opacity-10">
            <div className="absolute top-10 left-10 w-72 h-72 bg-white rounded-full blur-3xl" />
            <div className="absolute bottom-10 right-10 w-96 h-96 bg-white rounded-full blur-3xl" />
          </div>
          <div className="container mx-auto px-4 relative z-10">
            <div className="max-w-3xl">
              <h1 className="text-5xl mb-4">{contactData.title}</h1>
              <p className="text-xl text-white/90">
                We'd love to hear from you. Get in touch with us today!
              </p>
            </div>
          </div>
        </div>

        {/* Quick Contact Cards */}
        <div className="container mx-auto px-4 -mt-12 relative z-20 mb-12">
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6">
            <div className="bg-white rounded-xl shadow-lg p-6 hover:shadow-xl transition-shadow">
              <div
                className="h-12 w-12 rounded-lg flex items-center justify-center mb-4"
                style={{ backgroundColor: `${colors.primaryColor}15` }}
              >
                <MapPin className="h-6 w-6" style={{ color: colors.primaryColor }} />
              </div>
              <h3 className="text-lg font-medium mb-2" style={{ color: colors.textColor }}>Visit Us</h3>
              <p className="text-sm text-gray-600">{contactData.address}</p>
            </div>

            <div className="bg-white rounded-xl shadow-lg p-6 hover:shadow-xl transition-shadow">
              <div
                className="h-12 w-12 rounded-lg flex items-center justify-center mb-4"
                style={{ backgroundColor: `${colors.primaryColor}15` }}
              >
                <Phone className="h-6 w-6" style={{ color: colors.primaryColor }} />
              </div>
              <h3 className="text-lg font-medium mb-2" style={{ color: colors.textColor }}>Call Us</h3>
              <a href={`tel:${contactData.phone}`} className="text-sm text-gray-600 hover:underline">
                {contactData.phone}
              </a>
            </div>

            <div className="bg-white rounded-xl shadow-lg p-6 hover:shadow-xl transition-shadow">
              <div
                className="h-12 w-12 rounded-lg flex items-center justify-center mb-4"
                style={{ backgroundColor: `${colors.primaryColor}15` }}
              >
                <Mail className="h-6 w-6" style={{ color: colors.primaryColor }} />
              </div>
              <h3 className="text-lg font-medium mb-2" style={{ color: colors.textColor }}>Email Us</h3>
              <a href={`mailto:${contactData.email}`} className="text-sm text-gray-600 hover:underline break-all">
                {contactData.email}
              </a>
            </div>

            {contactData.workingHours && (
              <div className="bg-white rounded-xl shadow-lg p-6 hover:shadow-xl transition-shadow">
                <div
                  className="h-12 w-12 rounded-lg flex items-center justify-center mb-4"
                  style={{ backgroundColor: `${colors.primaryColor}15` }}
                >
                  <Clock className="h-6 w-6" style={{ color: colors.primaryColor }} />
                </div>
                <h3 className="text-lg font-medium mb-2" style={{ color: colors.textColor }}>Working Hours</h3>
                <p className="text-sm text-gray-600">{contactData.workingHours}</p>
              </div>
            )}
          </div>
        </div>

        {/* Main Content */}
        <div className="container mx-auto px-4 pb-16">
          <div className="grid grid-cols-1 lg:grid-cols-3 gap-8 max-w-7xl mx-auto">
            {/* Contact Form - Takes 2 columns */}
            <div className="lg:col-span-2">
              <div className="bg-white rounded-2xl shadow-xl p-8 md:p-10">
                <div className="flex items-center gap-3 mb-6">
                  <div
                    className="h-12 w-12 rounded-lg flex items-center justify-center"
                    style={{ backgroundColor: `${colors.primaryColor}15` }}
                  >
                    <MessageSquare className="h-6 w-6" style={{ color: colors.primaryColor }} />
                  </div>
                  <div>
                    <h2 className="text-3xl" style={{ color: colors.textColor }}>Send us a Message</h2>
                    <p className="text-gray-600">Fill out the form and we'll get back to you shortly</p>
                  </div>
                </div>

                <form onSubmit={handleSubmit} className="space-y-6">
                  <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
                    <div>
                      <Label className="text-base mb-2">Full Name *</Label>
                      <Input
                        value={formData.name}
                        onChange={(e) => setFormData({ ...formData, name: e.target.value })}
                        placeholder="John Doe"
                        className="h-12"
                        required
                      />
                    </div>

                    <div>
                      <Label className="text-base mb-2">Phone Number *</Label>
                      <Input
                        type="tel"
                        value={formData.phone}
                        onChange={(e) => setFormData({ ...formData, phone: e.target.value })}
                        placeholder="+91 98765 43210"
                        className="h-12"
                        required
                      />
                    </div>
                  </div>

                  <div>
                    <Label className="text-base mb-2">Email Address *</Label>
                    <Input
